function ready(t) {
    (document.attachEvent ? "complete" === document.readyState : "loading" !== document.readyState) ? t() : document.addEventListener("DOMContentLoaded", t);
}

function setPlatform() {
    -1 !== navigator.platform.indexOf("Mac") || -1 !== navigator.platform.indexOf("iPhone") ? document.querySelector("body").classList.add("ios") : document.querySelector("body").classList.add("android");
}

ready(setPlatform());